SELECT A.GNR_GRP_NAME, B.MGNRKEYVAL_ID, B.GNR_KEY, B.GNR_VAL, B.ORD_IN_GRP FROM MGNRGRP A, MGNRKEYVAL B WHERE A.MGNRGRP_ID = 1000001 AND A.MGNRGRP_ID = B.MGNRGRP_ID ORDER BY B.ORD_IN_GRP;
SELECT TACCOUNT_ID, ACCOUNT_NAME, PASSWORD, VERSION, DEL_FLG, CREATE_USER, CREATE_DATE, UPDATE_USER, UPDATE_DATE FROM TACCOUNT WHERE TACCOUNT_ID = 1000001;
SELECT A.SCR_NAME, B.TSCRELM_ID, B.SERVICE_NAME, ORD_IN_GRP FROM TSCR A, TSCRELM B WHERE A.TSCR_ID = 1000001 AND A.TSCR_ID = B.TSCR_ID ORDER BY B.ORD_IN_GRP;
