USE JLDB;

SET GLOBAL local_infile = true;
SHOW GLOBAL VARIABLES LIKE 'local_infile';






SELECT A.GNR_GRP_NAME, B.GNR_KEY_VAL_ID, B.GNR_KEY, B.GNR_VAL, B.ORD_IN_GRP FROM GNR_GRP A, GNR_KEY_VAL B WHERE A.GNR_GRP_ID = 1000001 AND A.GNR_GRP_ID = B.GNR_GRP_ID ORDER BY B.ORD_IN_GRP;
SELECT ACCNT_ID, ACCOUNT_NAME, PASSWORD, VERSION, IS_DELETED, CREATED_BY, CREATED_AT, UPDATED_BY, UPDATED_AT FROM ACCNT WHERE ACCNT_ID = 1000001;
SELECT A.SCR_NAME, B.SCR_ELM_ID, B.SERVICE_NAME, ORD_IN_GRP FROM SCR A, SCR_ELM B WHERE A.SCR_ID = 1000001 AND A.SCR_ID = B.SCR_ID ORDER BY B.ORD_IN_GRP;
